
f = open('Cable.txt', 'r').read()

mesures = f.split(' ')

# Test 1 : 1 -> 1 et -1 -> 0 :
test1 = ['0' if x == '-1' else '1' for x in mesures]
rslt1 = ''.join(test1)
#print(rslt1)

# Test 2 : 1 si changement de front, 0 sinon
test2 = []
for i in range(len(mesures)-1):
    if mesures[i] == mesures[i+1]:
        test2.append('0')
    else:
        test2.append('1')
rslt2 = ''.join(test2)
print(rslt2)
